package maan1;

public class Trio {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=0,b=1,c=2,d,n=10;
System.out.println(a +"\n" +b +"\n"+c);
 for(int i=1;i<=n-3;i++)
 {
	 d=a+b+c;
	 System.out.println(+d);
 a=b;
 b=c;
 c=d;
}	
}
}


		 
	
	
	

